    const end = new Date(endDate);
    const oneDay = 1000 * 60 * 60 * 24;
    const tripDays = Math.max(Math.round((end - start) / oneDay) - 1, 1);

    return `You are a travel planner AI. Create a detailed ${tripDays}-day travel itinerary for a trip to ${destination}. The travel dates are from ${startDate} to ${endDate}. Do NOT make up any other location or dates. Exclude arrival and departure days which are meant for flights. Generate the plan only for ${tripDays} full days between those dates. Include daily activities, food suggestions, costs, and travel tips. Stick strictly to the provided dates and location ‚Äî no hallucinations like Paris or October.`;
  })()
}}

