// AI response from OpenRouter is in:
// response.body.choices[0].message.content

return [
  {
    json: {
      itinerary: $json["choices"][0]["message"]["content"]
    }
  }
];

const start = new Date($json["start_date"]);
const end = new Date($json["end_date"]);
const dates = [];

for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
  const dateStr = new Date(d).toISOString().split('T')[0]; // e.g., "2025-06-25"
  dates.push({
    json: {
      start_date: dateStr,
      end_date: dateStr,
      lat: $json["lat"],
      lng: $json["lng"]
    }
  });
}

return dates;

// You're receiving multiple items (1 per day), each with a `daily` object
const result = [];

for (const item of items) {
  const daily = item.json.daily;

  result.push({
    json: {
      date: daily.time[0],
      summary: `üìÖ ${daily.time[0]}: üå°Ô∏è Max ${daily.temperature_2m_max[0]}¬∞C / Min ${daily.temperature_2m_min[0]}¬∞C, ‚òî Rain: ${daily.precipitation_sum[0]}mm`
    }
