const itinerary = $json["itinerary"] || "Itinerary not available.";
const destination = $json["destination"] || "your destination";
const startDate = $json["start_date"] || "start date";
const endDate = $json["end_date"] || "end date";

// If `weatherSummaries` is an array passed from previous node
const weatherItems = $items().filter(item => item.json.summary && item.json.date);

let weatherHtml = "<h2 style='color: #1e3799; font-size: 22px; margin-bottom: 10px;'>üå¶Ô∏è Weather Forecast</h2><div style='background-color: #fefefe; padding: 20px; border-radius: 10px; border: 1px solid #dcdde1; font-size: 16px; color: #2f3640; line-height: 1.6;'>";

if (weatherItems.length === 0) {
  weatherHtml += "No weather data available.";
} else {
  weatherItems.forEach(item => {
    weatherHtml += `${item.json.summary}<br>`;
  });
}

weatherHtml += "</div>";

// Convert itinerary \n to <br>
const formattedItinerary = itinerary
  .split(/Day \d+: /g)
  .filter(section => section.trim())
  .map((section, index) => {
    const lines = section.trim().split("\n").filter(line => line.trim());
    const dayTitle = `Day ${index + 1}: ${lines[0]}`;
    const activities = lines.slice(1).map(item => {
      if (/^\d{1,2}:\d{2}/.test(item)) {
        // starts with time like "9:00 AM"
        return `<li><strong>${item}</strong></li>`;
      } else {
        return `<li>${item}</li>`;
      }
    }).join("");

    return `
      <div style="margin-bottom: 25px;">
        <h3 style="color: #e67e22; font-size: 20px; margin-bottom: 8px;">üìÖ ${dayTitle}</h3>
        <ul style="padding-left: 20px; color: #2f3640; font-size: 15px; line-height: 1.5;">
          ${activities}
        </ul>
      </div>
    `;
  }).join("");

const itineraryHtml = `
  <div style="background-color: #f5f6fa; padding: 20px; border-radius: 10px; border: 1px solid #dcdde1; font-size: 16px; color: #2f3640; line-height: 1.6;">
    ${formattedItinerary}
  </div>
`;


const html = `
<div style="font-family: 'Segoe UI', Roboto, Arial, sans-serif; max-width: 700px; margin: auto; padding: 30px; background-color: #ffffff; border-radius: 12px; border: 1px solid #e0e0e0;">
